mapper.select:
SELECT count(a.id) AS total, SUM(CASE WHEN b.not_use = 0 THEN 1 ELSE 0 END) AS useTotal from pdf_dev_code a INNER JOIN t_device b on a.code_id = b.dev_type INNER JOIN t_org c on b.org_id = c.id
AND a.id = #{devType}
AND (c.parent_ids LIKE concat('%',#{orgId},'%') or c.id = #{orgId})

select COUNT(1) from t_org d inner join pdf_org p on p.t_org_id = d.id inner join sys_user_bureau ub on ub.bureau_id = p.bureau_id
ub.user_id =#{userId}
AND parent_ids LIKE concat('%',#{orgId},'%')
AND type = #{type}

select sum( case when (select MAX(c.warn) from t_device a inner JOIN t_channel b on b.dev_id = a.id inner JOIN t_real_data c on c.id = b.id where a.org_id = o.id) >0 then 1 else 0 end) from t_org o inner join pdf_org p on p.t_org_id = o.id inner join sys_user_bureau ub on ub.bureau_id = p.bureau_id
ub.user_id =#{userId}
AND o.parent_ids LIKE concat('%',#{orgId},'%')
AND o.type = #{type}

SELECT MAX(occur_time) from t_alarm_log a INNER JOIN t_channel b on a.ch_id = b.id inner join t_device td on td.id= b.dev_id INNER JOIN t_org c on c.id = td.org_id inner join pdf_org p on p.t_org_id = c.id inner join sys_user_bureau ub on ub.bureau_id = p.bureau_id
ub.user_id = #{userId}
AND c.parent_ids LIKE concat('%',#{orgId},'%')

select CONCAT(eb.parent_ids,eb.id) pIds, eb.id from pdf_electricity_bureau eb inner join (select bureau_id from pdf_org GROUP BY bureau_id) org on org.bureau_id = eb.id
inner join sys_user_bureau ub on eb.id = ub.bureau_id
ub.user_id = #{userId}
