mapper.update:
TRUNCATE t_device;

TRUNCATE t_device_detail;

TRUNCATE t_channel;

TRUNCATE t_real_data;

TRUNCATE pdf_org_image;

TRUNCATE pdf_image_dev_ch;

update t_device_detail
set online = #{online}
where mac = #{mac}

replace into t_channel( id, dev_id, type_id, ch_type, ch_no, name, coords_x, coords_y, param0, param1, param2, not_use, del_flag, create_date, update_date, online, outer_id, addr, channel_type, iec104
)VALUES( #{id}, #{dev_id}, #{type_id}, #{ch_type}, #{ch_no}, #{name}, #{coords_x}, #{coords_y}, #{param0}, #{param1}, #{param2}, #{not_use}, #{del_flag}, #{create_date}, #{update_date}, #{online}, #{outer_id}, #{addr}, #{channel_type},
0
#{iec104}
);
replace into t_real_data (id,real_value, real_time,start_time, warn) values ( #{id}, #{real_value}, #{real_time}, #{real_time},
0
#{warn}
);

update t_device_detail
set online = #{online}
where id = #{id};

update.foreach:
update t_real_data
set
real_value =#{item.real_value},
real_time = #{item.real_time}
where id=#{item.id};
