mapper.select:
SELECT b.name as parentName,
FROM t_device a
LEFT JOIN t_device b on b.id = a.parent_id WHERE a.id = #{id}

SELECT

FROM t_device a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.org_id = #{orgId}


		AND a.parent_id = #{parentId}


		AND a.dev_type = #{devType}


		AND a.type_id = #{typeId}


		AND a.name LIKE
		'%'||#{name}||'%'
		'%'+#{name}+'%'
		concat('%',#{name},'%')


		AND a.addr = #{addr}


		AND a.sn = #{sn}


		AND a.not_use = #{notUse}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

select ${nameConfig} from t_device a LEFT JOIN t_device_detail b on a.id = b.id LEFT JOIN t_code c on a.dev_type = c.id and a.type_id = c.type_id LEFT JOIN t_org d on a.org_id = d.id
a.del_flag = 0 AND a.org_id in (SELECT id from t_org where parent_ids like concat('%',#{orgId},'%') or id = #{orgId})
AND a.name like concat('%',#{name},'%')
AND c.name = #{codeName}
AND a.not_use = #{notUse}
order by a.create_date desc,a.id desc

mapper.update:
UPDATE t_device SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
select id,name from t_device where org_id = #{orgId} and coords_x IS NULL and
coords_y IS NULL
and dev_type=#{coldId}

