mapper.select:
select co.id devType, co.type_id typeId, co.name , IFNULL(tr.warn,0) warn, tc.`online`, CONCAT('static_modules/channel/',co.sideway_icon_skin) icon from t_channel tc left join t_code co on co.id = tc.channel_type and tc.type_id = co.type_id left join t_real_data tr on tr.id = tc.id where channel_type in (220,2,3) and tc.type_id in(9,4) and logic_org_id = #{orgId}
UNION all
select dev_type devType, td.type_id typeId, td.name, MAX(tr.warn) warn, tdd.online, CONCAT('static_modules/device/',co.sideway_icon_skin) icon from t_device td LEFT JOIN t_device_detail tdd on tdd.id =td.id left join t_code co on co.id = td.dev_type and td.type_id = co.type_id left join t_channel tc on tc.dev_id = td.id left join t_real_data tr on tr.id = tc.id where td.org_id = #{orgId} and dev_type in (162,168,169,172) GROUP BY td.id

