mapper.select:
SELECT 

FROM t_org_exec_unit_bind a






		ORDER BY ${page.orderBy}

mapper.insert:
INSERT INTO t_org_exec_unit_bind(
	id,
	org_id,
	exec_unit_id
) VALUES (
	#{id},
	#{orgId},
	#{execUnitId}
)

mapper.update:
UPDATE t_org_exec_unit_bind SET 	
	org_id = #{orgId},
	exec_unit_id = #{execUnitId}
WHERE id = #{id}

UPDATE t_org_exec_unit_bind SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
