mapper.select:
SELECT

FROM t_org a

WHERE
a.parent_id =0 or
a.code like concat('1035','%') and
a.del_flag = #{DEL_FLAG_NORMAL}

${sqlMap.dsf}
OR a.id = #{currentUser.office.area.id}
ORDER BY a.code

mapper.update:
update t_org set       
pic_url  = #{picUrl},             
image_name  = #{imageName} where id = #{orgId}

UPDATE t_org SET
parent_id = #{parent.id},
parent_ids = #{parentIds}
WHERE id = #{id}

mapper.select:

		select * from(

		SELECT
		CONCAT(a.id)  id,
		CONCAT(a.parent_id)  parentId,
		CONCAT(a.parent_ids,a.id)  parentIds,
		a.code,
		a.name,
		a.type,

		(select (select count(*) from t_device td where td.org_id = a.id and td.dev_type in
		(162,168,169,171,172,174)) +(select count(*) from t_channel tc where tc.logic_org_id = a.id and tc.channel_type in (2,3,202,220,225,240) and
		tc.type_id in (4,9))) deviceCount

		from
		t_org a 
		LEFT JOIN pdf_org b on a.id = b.t_org_id
		inner JOIN sys_user_bureau c on c.bureau_id = b.bureau_id
        LEFT JOIN pdf_principal p on p.pdf_id = a.id
		left join sys_user d on d.id = p.user_id

		     a.del_flag = 0 and a.type =5	

				 and c.user_id = #{userId}	


				and a.name like CONCAT('%',#{name},'%')


				and c.bureau_id =#{bureauId}


				and (a.parent_ids like CONCAT('%',#{orgId},'%') or a.id =#{orgId})


				and d.name like CONCAT('%',#{userName},'%')


				and b.level = #{level}


				and b.changer_num = #{changerNum}


				and b.rate = #{rate}


				and b.capacity = #{capacity}


				and b.declared_quantity = #{declaredQuantity}


				and b.controler  = #{controler}


			-------------------
				and b.high_voltage = #{highVoltage}


				and b.high_cabinet = #{highCabinet}


				and b.low_cabinet  = #{lowCabinet}










				and a.create_date >= CONCAT(#{createDate})


				and a.create_date <=CONCAT(#{updateDate})


		ORDER BY a.type,a.code) m 



				 and m.deviceCount	>= #{beginCount}


				 and m.deviceCount	<= #{endCount}

select eb.id bureauId, eb.name bureauName, CONCAT(o.id) as id, CONCAT(o.parent_id) AS parentId, o.parent_ids AS parentIds, o.code, o.name orgName, concat(d.id) userId, d.name userName, d.mobile, o.type, o.coords_x coordsX, o.coords_y coordsY, DATE_FORMAT(o.create_date,'%Y-%m-%d %h:%i') createDate, o.create_date createTime, b.address, b.level, b.changer_num changerNum, b.rate, capacity, declared_quantity declaredQuantity, controler,
(select (select count(*) from t_device td where td.org_id = o.id and td.dev_type in (162,168,169,171,172,174)) +(select count(*) from t_channel tc where tc.logic_org_id = o.id and tc.channel_type in (2,3,202,220,225,240) and tc.type_id in (4,9))) deviceCount
from t_org o LEFT JOIN pdf_org b on o.id = b.t_org_id LEFT JOIN pdf_principal c on c.pdf_id = o.id left join sys_user d on d.id = c.user_id LEFT JOIN pdf_electricity_bureau eb on eb.id = b.bureau_id where FIND_IN_SET(o.id,(
select GROUP_CONCAT(m.parent_ids,m.id) parentIds from (
SELECT a.*, (select (select count(*) from t_device td where td.org_id = a.id and td.dev_type in (162,168,169,171,172,174)) +(select count(*) from t_channel tc where tc.logic_org_id = a.id and tc.channel_type in (2,3,202,220,225,240) and tc.type_id in (4,9))) deviceCount from t_org a LEFT JOIN pdf_org b on a.id = b.t_org_id inner JOIN sys_user_bureau c on c.bureau_id = b.bureau_id LEFT JOIN pdf_principal p on p.pdf_id = a.id left join sys_user d on d.id = p.user_id
a.del_flag = 0 and a.type =5 and c.user_id = #{userId}
and a.name like CONCAT('%',#{name},'%')
and c.bureau_id =#{bureauId}
and a.id =#{orgId}
and d.name like CONCAT('%',#{userName},'%')
and b.level = #{level}
and b.changer_num = #{changerNum}
and b.rate = #{rate}
and b.capacity = #{capacity}
and b.declared_quantity = #{declaredQuantity}
and b.controler = #{controler}
and a.create_date >= CONCAT(#{createDate})
and a.create_date <=CONCAT(#{updateDate})
) m
and m.deviceCount >= #{beginCount}
and m.deviceCount <= #{endCount}
))
