mapper.update:
update
    t_alarm_log
set status = #{status}
where ch_id = #{chId}

mapper.select:
select CONCAT(a.id) orderId, CONCAT(a.id) id, CONCAT(a.dev_id) devId, a.alarm_type alarmType, a.prec, a.alarm_level alarmLevel, d.id typeId, DATE_FORMAT(a.alarm_time,'%Y-%m-%d %H:%i:%s') alarmTime, b.name alarmAddr, case when main_type = 0 and state in (2,3,4) then 0 end mainTpye, a.send_order_user sendOrderUser, a.confirm_user confirmUser, a.suggestion,a.principal, case when state = 4 and a.main_type = 1 then 6 else a.state end state, a.alarm_cacel_time alarmCancelTime, b.name orgName, c.name devName, d.name devTypeName, e.name sendName , e.mobile phone, a.alarm_source alarmSource from pdf_order a inner join t_device c on a.dev_id = c.id left join t_code d on c.dev_type = d.id and c.type_id = d.type_id left join t_org b on a.alarm_addr = b.id LEFT JOIN pdf_org pr on pr.t_org_id = b.id inner join sys_user_bureau sys on sys.bureau_id = pr.bureau_id left join sys_user e on sys.user_id = e.id
a.state >=2 and e.id = #{userId}
and a.order_id = #{id}
and a.id = #{orderId}
and c.name = #{devName}
and d.id = #{devType} and d.type_id = 1
and alarm_type = #{alarmType}
and a.alarm_level = #{alarmLevel}
and a.confirm_user = #{confirmUser}
and DATE_FORMAT(a.alarm_time,'%Y-%m-%d') > DATE_FORMAT(#{alarmStartTime},'%Y-%m-%d')
and DATE_FORMAT(a.alarm_time,'%Y-%m-%d') < DATE_FORMAT(#{alarmEndTime},'%Y-%m-%d')
and a.send_order_user in (select id from sys_user where name like CONCAT('%',#{sendOrderUser},'%'))
and a.state = #{state}
and (b.parent_ids like concat('%',#{alarmAddr},'%') or b.id = #{alarmAddr})
and a.principal like CONCAT('%',(select max(id) id from pdf_user where name like concat('%',#{pdfUser},'%')),'%')
and a.principal like CONCAT('%',(select max(id) id from pdf_maintenance_detail where contacts like concat('%',#{principal},'%')),'%')
order by (case when a.state =5 then 1 else 0 end),a.alarm_time desc

select org_id orgId
from t_device
where id = #{id}

mapper.update:
update pdf_order
set main_type = 1,
    principal = #{principal}
where id = #{id}
